// configuracion de proceso de tab en habitaciones
	$(function(){
		$('#obj_tab1').click(function(){
			remover_class_habi();
			remover_class_habi_tab();
			$(this).addClass('active first');
			$('#tabs1').removeClass('quicktabs-hide');			
		});
		$('#obj_tab2').click(function(){
			remover_class_habi();
			remover_class_habi_tab();
			$(this).addClass('active first');
			$('#tabs2').removeClass('quicktabs-hide');
		});
		$('#obj_tab3').click(function(){
			remover_class_habi();
			remover_class_habi_tab();
			$(this).addClass('active first');
			$('#tabs3').removeClass('quicktabs-hide');
		});
		$('#obj_tab4').click(function(){
			remover_class_habi();
			remover_class_habi_tab();
			$(this).addClass('active first');
			$('#tabs4').removeClass('quicktabs-hide');
		});
		$('#obj_tab5').click(function(){
			remover_class_habi();
			remover_class_habi_tab();
			$(this).addClass('active first');
			$('#tabs5').removeClass('quicktabs-hide');
		});
	});
	// funcion remover clas
	function remover_class_habi(){
		$('#obj_tab1').removeClass();
		$('#obj_tab2').removeClass();
		$('#obj_tab3').removeClass();
		$('#obj_tab4').removeClass();
		$('#obj_tab5').removeClass();
	}
	function remover_class_habi_tab(){
		$('#tabs1').addClass(' quicktabs-hide');
		$('#tabs2').addClass(' quicktabs-hide');
		$('#tabs3').addClass(' quicktabs-hide');
		$('#tabs4').addClass(' quicktabs-hide');
		$('#tabs5').addClass(' quicktabs-hide');
	}